mapper.select:
    	SELECT DISTINCT products.p_id FROM own.product_basic_attr,own.products 
    	where products.p_id=product_basic_attr.p_id
    	and products.p_nowprice >= #{startPrice} 
    	and products.p_nowprice <= #{endPrice} 
    	and products.p_categoryid in

		#{item}



	    	and product_basic_attr.attr_value_id in 

				#{item}


		group by p_id desc 

			limit #{begain},#{size}

    	SELECT p.p_id FROM own.products as p 
    	where p.p_status=1 and p.p_nowprice >= #{startPrice} 
    	and p.p_nowprice <= #{endPrice}  
    	and p.p_categoryid in

		#{item}

		order by p_id desc 	limit #{begain},#{size}

    	SELECT p.p_id FROM own.products as p 
    	where p.p_status=1 and p.p_nowprice >= #{startPrice} 
    	and p.p_nowprice <= #{endPrice}


    			and p.p_hot=1


    			and p.p_recommend=1


    			and p.p_now=1


		order by p_id desc 	limit #{begain},#{size}

